<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{    protected $title;		// заголовок страницы    protected $content;		// содержание страницы    protected $navbar;		// левой блок    //    // Конструктор.    //    function __construct()    {        $this->startup();    }    protected function before()    {        $mCats = M_Categories::instance();        $navbar = $mCats->all_cats_subcats();        $this->navbar = $this->Template('view/v_navbar.php', array('navbar' => $navbar));        $this->title = 'Tempo';        $this->content = '';    }    //    // Генерация базового шаблона.    //	    public function render()    {        $vars = array('title' => $this->title,                                  'navbar' => $this->navbar,                                  'content' => $this->content);	        $page = $this->Template('view/v_main.php', $vars);				        echo $page;    }	    //    // Подключение к бд.    //    protected function startup()    {        // Открытие сессии.        session_start();    }}