<?php//// Конттроллер страницы чтения.//class C_Item extends C_Base{    //    // Конструктор.    //    function __construct()    {        parent::__construct();    }    // Главная страница.    public function action_index()    {        $this->title .= ' Official';        // Получение экземпляра модели.        $mItems = M_Items::instance();        // Извлечение товаров с пагинацией.        $items = $mItems->allPaged($_GET['subcat'], 4);        // Количество товаров в ряд.        $td_number = 4;        $this->content = $this->Template('view/v_index.php', array('items' => $items, 'td_number' => $td_number));	    }    // Страница добавления товара.    public function action_add()    {        $this->title .= ' add';        $this->navbar = '';                // Получение экземпляра модели.        $mUsers = M_Users::instance();                // Проверка авторизации.        if (!$mUsers->checkAuth())        {            header("Location: index.php?c=user&act=auth");            exit();        }                // Получение экземпляра модели.        $mItems = M_Items::instance();        $mCats = M_Categories::instance();                $categories = $mCats->all_cats_subcats();                if ($this->IsPost())        {            if ($mItems->add($category, $_POST['title'], $_POST['desc'], $img))            {                header("Location: index.php?c=item&act=get&");                die();            }        }                $categories = $mCats->all_cats_subcats();                $this->content = $this->Template('view/v_add.php', array('categories' => $categories));    }    // Страница конкретного товара.    public function action_item()    {    }}