<?php//error_reporting(E_ALL);function __autoload($name) {    switch ($name[0])    {        case 'C':            include_once('controller/' . $name . '.php');            break;        case 'M':            include_once('model/' . $name . '.php');    }}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';// switch ($_GET['c']){    case 'item':        $controller = new C_Item();        break;    case 'user':        $controller = new C_User();        break;    default:        $controller = new C_Item();}$controller->Request($action);