<?php//// Модель для работы со статьями.//class M_Comments{	// Ссылка на экземпляр класса.	private static $instance;	private $msql;		// Получение единственного экземпляра.	public static function instance()	{		if (self::$instance == null)			self::$instance = new M_Comments;				return self::$instance;	}		public function __construct()	{		$this->msql = M_MSQL::instance();	}	//	// Комментарии к конкретной статье.	//	public function get($id_article)	{		// Подготовка.		$id_article = intval($id_article);				// Запрос.		$result = "SELECT * FROM comments				   WHERE art_id = '$id_article'				   ORDER by cmt_id DESC";		return $this->msql->Select($result);	}	//	// Добавить комментарий.	//	public function add($name, $text, $id_article)	{		// Подготовка.		$name = trim($name);		$text = trim($text);		$id_article = intval($id_article);		// Проверка.		if ($name == '' || $text == '')			return false;		// Запрос.		$obj = array();		$obj['name'] = $name;		$obj['text'] = $text;		$obj['art_id'] = $id_article;				return $this->msql->Insert('comments', $obj);	}}